        var hasher = Secp256k1sSha256()
        
        for i in 0..<inputs.count {
            let inputBytes = [UInt8](inputs[i].utf8)
            hasher.write(bytes: inputBytes)
            let hash = hasher.finalize()
            XCTAssertEqual(hash, outputs[i], "test \(i)")
